import '../styles/Favoris.css'
import { useState } from 'react'


function Favoris(){
    const author = 8
    const [favoris, updateFavoris] = useState(0)
    const [isOpen, setIsOpen] = useState(false)

    return (
        <div className="bg-favoris">
            <button onClick={() => setIsOpen(false)} >Ferme</button>
            <h2>Articles favoris</h2>
            <div>
                post : {author}
                <button onClick={() => updateFavoris(favoris + 1)}>
                    Ajouter
                </button>
            </div>
            <h3> Total : {author * favoris}</h3>    
        </div>
    )
}

export default Favoris
